<?php
function getBadge($badgeUrl) {
    // 使用cURL进行请求
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $badgeUrl);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

    // 获取响应内容
    $response = curl_exec($ch);
    curl_close($ch);

    return $response;
}

$badgeUrl = 'https://img.shields.io/badge/Hosted-腾讯云-0000FF?logo=data:image/svg+xml;base64,&logoColor=white';
echo getBadge($badgeUrl);
// 如果没有指定badge参数，可以返回错误信息或者默认页面
// echo "No badge specified";
?>